mapper.select:

SELECT userNo, userName, id
FROM users
WHERE id = #{value}


SELECT cateName, userNo ,cateNo, description, regDate
FROM category
WHERE userNo = #{value}


SELECT COUNT(postTitle)
FROM post
WHERE cateNo = #{value}


SELECT userNo, blogTitle, logoFile
FROM blog
WHERE userNo = #{value}

mapper.update:

UPDATE blog
SET blogTitle=#{blogTitle}
WHERE userNo = #{userNo}

mapper.select:

SELECT userNo, cmtContent, regDate, cmtNo
FROM comments
WHERE postNo = #{value}
